mapper.select:
SELECT id, post_id AS postId, contents, rdate, stdn_id AS stdnId
FROM comm
WHERE stdn_id=#{stdnId}

SELECT id, post_id AS postId, contents, rdate, stdn_id AS stdnId
FROM comm
WHERE post_id=#{postId}

SELECT id, post_id AS postId, contents, rdate, stdn_id AS stdnId
FROM comm
WHERE id=#{id}

mapper.update:
UPDATE comm
SET contents=#{contents}
WHERE id=#{id}
