mapper.select:
SELECT id, mbti, comdate, digidate, rdate, detail, insta, facebook
FROM my_page
WHERE ID=#{id}

mapper.update:
UPDATE my_page SET mbti=#{mbti}, comdate=#{comdate}
              , digidate=#{digidate}, detail=#{detail}
              , insta=#{insta}, facebook=#{facebook}
WHERE id=#{id}
