mapper.update:
UPDATE stdn SET name=#{name}, email=#{email}
              , contact=#{contact}, img=#{img}
              , birthday=#{birthday}, sbj1=#{sbj1}
              , sbj2=#{sbj2}, sbj3=#{sbj3}
WHERE id=#{id}

UPDATE stdn SET name=#{name}, email=#{email}
              , contact=#{contact}, img=#{img}
              , birthday=#{birthday}, is_subsc=#{isSubsc}
              , cpn=#{cpn}, is_exit=#{isExit}
              , exit_date=#{exitDate}, last_visit=#{lastVisit}
              , is_join=#{isJoin}, sbj1=#{sbj1}
              , sbj2=#{sbj2}, sbj3=#{sbj3}
              , login_error=#{loginError}
WHERE id=#{id}
